","section {
  // Default card styles.
  .card {
    .card-body {
      position: relative;
      color: text-color();
      padding: 1.5rem;

      @include media-breakpoint-up(lg) {
        //padding: 1.75rem;
      }

      h1,
      h2,
      h3,
      h4,
      h5 {
        color: text-color();
      }

      .headline {
        margin-bottom: 1.25rem;

        @include media-breakpoint-up(lg) {
          margin-bottom: 1.5rem;
        }

        .superheadline {
          color: text-color("light");
          text-transform: uppercase;
        }

        .subheadline {
          color: text-color("light");
        }

        &:last-child {
          margin-bottom: 0;
        }
      }

      > * {
        margin-top: 0.75rem;
        margin-bottom: 0.75rem;

        &:first-child {
          margin-top: 0;
        }

        &:last-child {
          margin-bottom: 0;
        }
      }

      p {
        &:last-child {
          margin-bottom: 0;
        }
      }
    }

    &.flat {
      border: none !important;
      box-shadow: none !important;
    }

    &.bottom-highlight {
      position: relative;
      padding-bottom: 85px;
      //margin-bottom: 25px;

      &::after {
        content: "";
        width: 48px;
        position: absolute;
        height: 2px;
        bottom: 35px;
        left: 0;
        background-image: linear-gradient(to right, #bf1819, rgba(128, 0, 128, 0.8));
      }
    }

    &.transparent {
      background-color: transparent !important;

      .card-body {
        color: inherit;

        h1,
        h2,
        h3,
        h4,
        h5,
        .headline {
          color: inherit;
          letter-spacing: -0.1px;
        }
      }
    }

    &.no-y-padding {
      .card-body {
        padding-top: 0 !important;
        padding-bottom: 0 !important;

        .icon-wrapper {
          top: 0 !important;
        }
      }

      figure {
        &.figure-cards {
          padding-top: 0;
          padding-bottom: 1.25rem;
          top: 0 !important;
        }
      }
    }

    &.no-x-padding {
      .card-body {
        padding-left: 0 !important;
        padding-right: 0 !important;
      }

      figure {
        &.figure-cards {
          padding-left: 0;
          padding-right: 0;
          left: 0 !important;
        }
      }

      &.card-icon-image-left,
      &.card-icon-image-lg-left,
      &.card-icon-image-xl-left,
      &.card-icon-image-xxl-left,
      &.card-icon-image-xxxl-left,
      &.card-icon-image-xxxxl-left,
      &.card-icon-image-xxxxxl-left {
        .card-body {
          padding-left: 1.25rem !important;
        }
      }

      &.card-icon-image-right,
      &.card-icon-image-lg-right,
      &.card-icon-image-xl-right,
      &.card-icon-image-xxl-right,
      &.card-icon-image-xxxl-right {
        .card-body {
          padding-right: 1.25rem !important;
        }
      }
    }

    &.no-border {
      border: none !important;
    }

    &.square-card {
      border-radius: 0 !important;

      .figure-cards {
        img {
          border-radius: 0 !important;
        }
      }
    }

    &.dark-border {
      border-color: border-color("border-dark");
    }

    &.light-border {
      border-color: border-color("border-light");
    }

    &.lighter-border {
      border-color: border-color("border-lighter");
    }

    &.padding-wrap-1 {
      > .wrapper-card-content {
        padding: 0.5rem;

        @include media-breakpoint-up(md) {
          padding: 0.75rem;
        }

        @include media-breakpoint-up(lg) {
          padding: 1rem;
        }
      }
    }

    &.padding-wrap-2 {
      > .wrapper-card-content {
        padding: 1rem;

        @include media-breakpoint-up(md) {
          padding: 1.5rem;
        }

        @include media-breakpoint-up(lg) {
          padding: 2rem;
        }
      }
    }

    &.stat-headline {
      &.hl-xl,
      .hl-xl {
        h1.headline {
          .superheadline {
            font-size: 0.35em;
          }

          .subheadline {
            font-size: 0.35em;
          }
        }

        h2.headline {
          .superheadline {
            font-size: 0.4em;
          }

          .subheadline {
            font-size: 0.4em;
          }
        }

        h3.headline {
          .superheadline {
            font-size: 0.45em;
          }

          .subheadline {
            font-size: 0.45em;
          }
        }

        h4.headline {
          .superheadline {
            font-size: 0.5em;
          }

          .subheadline {
            font-size: 0.5em;
          }
        }

        h5.headline {
          .superheadline {
            font-size: 0.6em;
          }

          .subheadline {
            font-size: 0.6em;
          }
        }
      }

      .wrapper-card-content {
        height: 100%;

        .card-body {
          height: 100%;

          .headline {
            height: 100%;
            padding: 0;
            margin: 0;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
          }
        }
      }
    }
  }

  .col-card {
    .icon-wrap-icon-pos-top {
      margin-bottom: 2rem;
    }

    .col-inside-icon-pos-left {
      .card {
        .card-body {
          padding-left: 2.25rem !important;

          .icon-wrapper {
            width: 2rem;
            position: absolute;
            top: 1.25rem;
            left: 0;
            text-align: center;

            & + div {
              margin-top: 0;
            }
          }
        }
      }

      &.col-inside-fa-2x {
        .card {
          .card-body {
            padding-left: 4.5rem !important;

            .icon-wrapper {
              width: 4rem;
            }
          }
        }
      }

      &.col-inside-fa-3x {
        .card {
          .card-body {
            padding-left: 6rem !important;

            .icon-wrapper {
              width: 5rem;
            }
          }
        }
      }

      &.col-inside-fa-4x {
        .card {
          .card-body {
            padding-left: 7rem !important;

            .icon-wrapper {
              width: 6rem;
            }
          }
        }
      }

      &.col-inside-fa-5x {
        .card {
          .card-body {
            padding-left: 8.5rem !important;

            .icon-wrapper {
              width: 7rem;
            }
          }
        }
      }

      &.col-inside-icon-40px {
        .card {
          .card-body {
            padding-left: 80px !important;

            .icon-wrapper {
              width: 60px;
            }
          }
        }
      }
    }
  }

  .col-cards {
    > .col-inside {
      > .row {
        > .col-card {
          .card {
            &.logo-sq-box {
              border-radius: 0;

              figure {
                padding: 1.8rem;

                @include media-breakpoint-up(sm) {
                  padding: 2rem;
                }

                @include media-breakpoint-up(md) {
                  padding: 3rem;
                }

                @include media-breakpoint-up(lg) {
                  padding: 3.8rem;
                }
              }
            }
          }

          @include media-breakpoint-up(md) {
            &:nth-child(odd) {
              .card {
                //&.staggered-logos-lg {
                //  margin-top: 60px;
                //  margin-left: -1px;
                //}
              }
            }

            &:nth-child(even) {
              .card {
                &.staggered-logos {
                  margin-top: -60px;
                  margin-bottom: 60px;
                }
              }
            }
          }
        }

        &.gutter-none {
          > .col-card {
            .card {
              &.staggered-logos {
                @include media-breakpoint-down(sm) {
                  border: none !important;
                }

                @include media-breakpoint-up(md) {
                  margin-left: -1px;
                }
              }
            }
          }
        }
      }
    }

    &.stacked-divider {
      > .col-inside {
        > .row {
          > .col-card {
            > .col-inside {
              border-bottom: 1px solid border-color("lighter");
            }

            &:first-child {
              > .col-inside {
                border-top: none;
                padding-top: 0 !important;
              }
            }

            &:last-child {
              > .col-inside {
                border-bottom: none;
                padding-bottom: 0 !important;
              }
            }
          }
        }
      }
    }
  }
}
