.bg-overlay-lighter,
.bg-overlay-light,
.bg-overlay-darker,
.bg-overlay-dark {
  position: relative;

  &::before {
    content: "";
    z-index: 0;
    position: absolute;
    width: 100%;
    height: 100%;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
  }
}

*.bg-overlay-light {
