GhK4rzCH;CACF","file":"main.scss","sourcesContent":["/** Import Bootstrap functions */
@import "~bootstrap/scss/functions";

$theme-colors: (
  primary: #216dc5,
  primary-light: lighten(#216dc5, 10),
  secondary: #6c6463,
  light: #dbdde2,
  lighter: #f2f4f9,
  white: #fff,
  dark: #1f2532,
  darker: #10131a,
  secondary-orange: rgba(255, 127, 31, 1),
  secondary-green: rgba(0, 180, 125, 1),
  secondary-purple: rgba(104, 68, 205, 1),
  //secondary-light: lighten(#6c6463, 25),
  //secondary-lighter: lighten(#6c6463, 10),
  //secondary-darker: darken(#6c6463, 5),
  //secondary-dark: darken(#6c6463, 10),
  //primary-light: lighten(#e73c3e, 15),
  //primary-lighter: lighten(#e73c3e, 7.5),
  //primary: #e73c3e,
  //primary-darker: darken(#e73c3e, 5),
  //primary-dark: darken(#e73c3e, 10),
  gray-dark: darken(#bababa, 25),
  gray-darker: darken(#bababa, 10),
  gray: #bababa,
  gray-lighter: lighten(#bababa, 5),
  gray-light: lighten(#bababa, 10),
  //dark: #071721,
  medium: darken(#f1f1f1, 20),
  theme-white: #fff,
  text-light: #747985,
  text-lighter: #8c919e,
  blog-green: #00cba6,
  blog-blue: #4860ff,
  blog-red: #f2434f,
  blog-purple: #7469ab,
  blog-gray: #a7abb5,
);

/** Bootstrap navbar fix (https://git.io/fADqW) */
$navbar-dark-toggler-icon-bg: none;
$navbar-light-toggler-icon-bg: none;

$grid-breakpoints: (
  xs: 0,
  sm:576px,
  md:768px,
  lg:1024px,
  xl:1200px,
  xxl:1440px,
  xxxl:1920px,
  //xxxxl:2400px
) !default;

$container-max-widths: (
  sm:540px,
  md:720px,
  lg:994px,
  xl:1170px,
  xxl:1280px,
  xxxl:1400px,
  //xxxxl:1600px
) !default;

$border-radius: 0.15rem;

// Update bootstrap spacer classes.
// https://medium.com/@louiswang0128/expand-bootstrap-spacing-classes-with-sass-ced4b5383aea
$spacer: 1rem !default;
$spacers: () !default;
$spacers: map-merge(
  (
    0: 0,
    1: ($spacer * 0.5),
    2: $spacer,
    3: ($spacer * 1.5),
    4: ($spacer * 2),
    5: ($spacer * 2.5),
    6: ($spacer * 3),
    7: ($spacer * 3.5),
    8: ($spacer * 4),
    9: ($spacer * 4.5),
    10: ($spacer * 5),
    11: ($spacer * 5.5),
    12: ($spacer * 6),
    13: ($spacer * 6.5),
    14: ($spacer * 7),
    15: ($spacer * 7.5),
    16: ($spacer * 8),
  ),
  $spacers
);

// Buttons + Forms
//
// Shared variables that are reassigned to `$input-` and `$btn-` specific variables.
$input-btn-padding-y: 0.2rem !default;
$input-btn-padding-x: 0.5rem !default;
$input-btn-font-size: 14px !default;
$input-btn-line-height: 1.6 !default;
$input-btn-padding-y-sm: 0.125rem !default;
$input-btn-padding-x-sm: 0.35rem !default;
$input-btn-font-size-sm: 13px !default;
$input-btn-padding-y-lg: 0.4rem !default;
$input-btn-padding-x-lg: 0.8rem !default;
$input-btn-font-size-lg: 15px !default;

//$btn-box-shadow: inset 0 1px 0 rgba($white, 0.15), 0 1px 1px rgba($black, 0.075) !default;
$btn-box-shadow: 0 2px 0 rgba(0, 0, 0, 0.045) !default;

// Buttons
//
// For each of Bootstrap's buttons, define text, background, and border color.

$btn-padding-y:               $input-btn-padding-y !default;
$btn-padding-x:               $input-btn-padding-x !default;
$btn-font-size:               $input-btn-font-size !default;
$btn-line-height:             $input-btn-line-height !default;

$btn-padding-y-sm:            $input-btn-padding-y-sm !default;
$btn-padding-x-sm:            $input-btn-padding-x-sm !default;
$btn-font-size-sm:            $input-btn-font-size-sm !default;

$btn-padding-y-lg:            $input-btn-padding-y-lg !default;
$btn-padding-x-lg:            $input-btn-padding-x-lg !default;
$btn-font-size-lg:            $input-btn-font-size-lg !default;

//$btn-focus-box-shadow:        $input-btn-focus-box-shadow !default;
//$btn-active-box-shadow:       inset 0 3px 5px rgba($black, .125) !default;

// Allows for customizing button radius independently from global border radius
//$btn-border-radius:           $border-radius !default;
//$btn-border-radius-sm:        $border-radius-sm !default;
//$btn-border-radius-lg:        $border-radius-lg !default;
","/** Import Bootstrap functions */
@import "~bootstrap/scss/functions";

$text-lg: 1.5rem;

$headline-size: (
  large: 60px
);

$font-fallback: (
  serif: "serif",
  sans-serif: "sans-serif"
);

$font-midstack: (
  serif: "poynter-oldstyle-display",
  sans-serif: "Arial"
);

$font-main: (
  serif: "Times New Roman",
  sans-serif: "proxima-nova",
  headline: "NeuzeitSLTStd-BookHeavy",
  headline-light: "NeuzeitSLTStd-Book",
  tab-nav: "proxima-nova",
  superheadline: "NeuzeitSLTStd-Book",
  subheadline: "NeuzeitSLTStd-Book",
  list-headline: "NeuzeitSLTStd-BookHeavy",
  list-text: "proxima-nova",
  nav: "proxima-nova",
  nav-headline: "proxima-nova",
  nav-desc: "proxima-nova",
  body: "proxima-nova",
  cta: "proxima-nova",
  quote: "proxima-nova",
  quote-name: "proxima-nova",
  quote-details: "proxima-nova",
  strong: "proxima-nova",
  section-header-light: "NeuzeitSLTStd-Book"
);

$link-colors: (
  primary: theme-color("primary"),
  secondary: theme-color("secondary"),
  dark: theme-color("dark"),
  darker: theme-color("darker"),
  link-lighter: #8c919e,
  link-lighter-hover: darken(#8c919e, 10),
  link-lighter-light-hover: lighten(#8c919e, 10),
  link-light: #717885,
  link-light-hover: darken(#717885, 10),
  link-light-light-hover: lighten(#717885, 10),
  link-dark: lighten(#1f2532, 20),
  link-dark-hover: lighten(#1f2532, 10),
  link-dark-light-hover: lighten(#1f2532, 35),
  link-white: #fff,
  link-gray-light: darken(#fff, 15),
  link-blue: #1d91d0,
  link-blue-hover: darken(#1d91d0, 20),
  link-primary: theme-color("primary"),
  link-primary-hover: theme-color("primary-dark"),
  light: theme-color("text-light"),
  lighter: theme-color("text-lighter"),
  //link-secondary: #6c6463,
  //link-secondary-hover: darken(#6c6463, 10),
  //link-secondary-light-hover: lighten(#6c6463, 10)
);

$text-colors: (
  text-white: #fff,
  //dark: #0d2c3d,
  dark: theme-color("dark"),
  darker: theme-color("darker"),
  medium: #4f4f4f,
  light: theme-color("text-light"),
  lighter: theme-color("text-lighter"),
  primary: theme-color("primary"),
  secondary: theme-color("secondary"),
  nav-link-desc: #585e6f,
