.tabs {
  &[data-nav-mobile-slider] {
    .tns-nav {
      display: none !important;
    }

    .tns-outer {
      position: relative;

      .tns-controls {
        width: 100%;
        left: 0;
        right: 0;
        position: absolute;
        height: 1px;

        > button {
          border: none;
          border: 1px solid theme-color("medium");
          outline: none;
          flex: 1;
          background-color: transparent;
          height: 36px;
          width: 36px;
          border-radius: 100%;
          color: theme-color("medium");

          &.tns-nav-active {
            border-color: theme-color("medium");
          }
        }

        [data-controls="prev"] {
          position: absolute;
          left: 0;
          margin-top: -36px;
        }

        [data-controls="next"] {
          position: absolute;
          right: 0;
          margin-top: -36px;
        }

        @include media-breakpoint-up(md) {
          display: none !important;
        }
      }
    }
  }

  .nav-wrapper {
    position: relative;

    ul {
      &.nav {
        li {
          padding: 0;
          margin: 0.5rem 1.2rem;
          position: relative;

          @include media-breakpoint-up(xl) {
            margin: 0.5rem 1.4rem;
          }

          &:first-child {
            margin-left: 0;
          }

          a {
            font-size: 14px;
            font-family: font-main("tab-nav"), font-midstack("sans-serif"), font-fallback("sans-serif");
            letter-spacing: 2px;
            text-decoration: none;
            margin: 0;
            padding: 0.75rem 0.3rem;
            position: relative;
            text-transform: uppercase;

            @include media-breakpoint-up(md) {
              font-size: 13px;
              letter-spacing: 1.35px;
              padding: 0.75rem 0.4rem;
            }

            @include media-breakpoint-up(lg) {
              font-size: 14px;
              letter-spacing: 1.65px;
              padding: 0.75rem 0.25rem;
            }

            @include media-breakpoint-up(xl) {
              letter-spacing: 2px;
              padding: 0.75rem 0.3rem;
            }

            &:hover,
            &.active {
              &::after {
                content: "";
                width: 100%;
                position: absolute;
                height: 2px;
                bottom: 0;
                left: 0;
                right: 0;
                background-color: theme-color("medium");
                //background-image: linear-gradient(to right, #bf1819, rgba(128, 0, 128, 0.8));
              }
            }

            &:not(.active) {
              //color: link-color("link-white");
              color: link-color("link-light");

              &:hover {
                //color: link-color("link-white");
                //color: link-color("link-light");
                color: link-color("link-light-hover");
              }
            }
          }
        }

        &.nav-mobile-slider {
          @include media-breakpoint-down(md) {
            li {
              padding: 0;
              margin: 0;
              position: relative;
              text-align: center;

              a {
                text-align: center;
                margin-left: auto;
                margin-right: auto;
              }
              //&:first-child {
              //  margin-left: 0;
              //}
            }
          }
        }
      }
    }

    &.tabs-nav-center {
      ul {
        &.nav {
          &.nav-mobile-slider {
            @include media-breakpoint-up(md) {
              display: flex;
              justify-content: center;
            }
          }
        }
      }
    }

    &.tabs-nav-right {
