@keyframes slideIn {
  0% {
    transform: translate(-50%, -1rem);
    opacity: 0;
  }

  100% {
    transform: translate(-50%, 0);
    opacity: 0.5;
  }

  0% {
    transform: translate(-50%, -1rem);
    opacity: 0;
  }
}

@keyframes slideInDefault {
  0% {
    transform: translate(0, -1rem);
    opacity: 0;
  }

  100% {
    transform: translate(0, 0);
    opacity: 1;
  }

  0% {
    transform: translate(0, -1rem);
    opacity: 0;
  }
}

.dropdown-animate {
  .slideIn {
    -webkit-animation-name: slideInDefault;
    animation-name: slideInDefault;
  }
}

.dropdown-menu-full,
.dropdown-menu-center {
  .slideIn {
    -webkit-animation-name: slideIn;
    animation-name: slideIn;
  }
}
