.mktoFormRow {
  float: none;
  clear: none;

  // Remove/overwrite some default maketo display on form items.
  .mktoFormRow,
  .mktoFieldDescriptor,
  .mktoFormCol {
    float: none;
    clear: none;
  }

  // Default input styling.
  input {
    width: 100%;
    border-radius: 0;
    border-color: transparent;
    border-bottom-color: color-border("qc-gray");
    background-color: transparent;

    &[type=checkbox] {
      max-width: 12px;
    }
    //&[type=radio]{
    //  max-width: 12px;
    //}
  }

  //////
  // Basic Marketo Form Field Styling
  //////
  textarea,
  input[type=email],
  input[type=tel],
  input[type=text] {
    font-size: 14px;
    font-family: "proxima-nova", "Helvetica Neue", Arial, Verdana, sans-serif;
    color: #212934;
    border-radius: 0;
    height: 36px;
    padding: 2px 10px 0 2px;
    box-sizing: border-box;
    width: 100%;
    border: 1px solid transparent;
    border-bottom-color: color-border("qc-gray");
    transition: 250ms box-shadow;
    background-color: transparent;

    &:focus {
      outline: 0;
      border-bottom-color: #206dc5;
      box-shadow: 0 0 4px 0 rgba(53, 170, 216, 0.9);
      box-shadow: none;
    }
  }

  // Default checkbox list styling.
  .mktoCheckboxList {
    input,
    label {
      display: inline-block;
      vertical-align: middle;
      margin: 5px;
      cursor: pointer;
    }

    input {
      opacity: 0;
      position: absolute;
    }

    label {
      position: relative;
      left: 0;
    }

    & > label {
      margin-left: 0;
    }
  }

  // Remove/overwrite some marketo display items from forms.
  .mktoClear,
  .mktoOffset {
    display: none;
  }

  // Remove/overwrite some default maketo display on form items.
  .mktoFieldDescriptor {
    display: block;
    padding: 0;
    margin: 0;
  }

  // Default marketo form error.
  .mktoErrorMsg {
    font-size: 0.85rem;
    padding: 0;
    line-height: 1.2;
    margin: 4px 0 0 4px;
    width: 100%;
    max-width: 90%;
  }

  .mktoError {
    width: 100%;
  }

  .mktoField {
    &.mktoInvalid {
      border-color: transparent;
      border-bottom-color: red;
    }
  }

  .mktoFieldWrap {
    min-height: 58px; // !!! this is very F2PF to change and needs to be made more dynamic.
    position: relative;
    overflow: hidden;
    display: flex;
    flex-wrap: wrap;
    //flex-direction: column;

    label {
      position: absolute;
      left: -9999px;

      &.mktoLabel {
        &[for=expressConsent] {
          left: 0;
          position: relative;
          order: 2;
          width: calc(100% - 25px) !important;
          flex: 1 1 calc(100% - 25px) !important;
          padding: 0;
          margin: 0;
          line-height: 1.35;
          font-size: 0.9rem;

          a {
            color: color-text("link") !important;

            &:hover,
            &:active,
            &:focus {
              color: color-text("link-hover") !important;
            }
          }

          & + .mktoGutter {
            order: 10;

            & + .mktoCheckboxList {
              order: 1;
              float: none;
              width: 25px !important;
              flex: 1 1 25px !important;
              padding: 0;
              margin: 0;

              input {
                opacity: 1;
                position: relative;
                margin: 5px 0;
              }
            }
          }
        }
      }
    }
  }

  //////
  // The person_notes texarea field.
  //////
  &.mktopersonnotes_textarea_row {
    margin-bottom: 1rem;
  }
}

form {
