  }
}

body {
  &.contact-us,
  &.contact-quantcast,
  &.contact {
    overflow: hidden;

    .contact-takeover {
      transition: all 0 linear !important;
      position: fixed;
      top: 75px !important;
      height: calc(100vh - 75px) !important;
      z-index: 99 !important;

      @include media-breakpoint-up("lg") {
        top: 83px !important;
        height: calc(100vh - 83px) !important;
      }

      .close {
        display: none !important;
      }
    }
  }

  &.contact-show {
    overflow: hidden;

    .contact-takeover {
      position: fixed;
      top: 0;
      height: 100vh;
      z-index: 9999;
    }
  }
}
