@import "~bootstrap/scss/functions";

$theme-colors: (
  secondary-light: lighten(#6c6463, 25),
  secondary-lighter: lighten(#6c6463, 10),
  secondary: #6c6463,
  secondary-darker: darken(#6c6463, 5),
  secondary-dark: darken(#6c6463, 10),
  primary-light: lighten(#e73c3e, 15),
  primary-lighter: lighten(#e73c3e, 7.5),
  //primary: #e73c3e,
  primary: #f2434f,
  primary-darker: darken(#e73c3e, 5),
  primary-dark: darken(#e73c3e, 10),
  gray-dark: darken(#bababa, 25),
  gray-darker: darken(#bababa, 10),
  gray: #bababa,
  gray-lighter: lighten(#bababa, 5),
  gray-light: lighten(#bababa, 10),
  //dark: #071721,
  dark: #1f2532, // 1f2532 is the quantcast theme primary dark
  darker: #10131a,
  medium: darken(#f1f1f1, 20),
  light: #dbdde2,
  lighter: #f2f4f9,
  theme-white: #fff,
  text-light: #747985,
  text-lighter: #8c919e,
  secondary-yellow: #fbeaa3,
  secondary-green: #00cba6,
  secondary-purple: #2d1d80,
  blog-green: #00cba6,
  blog-blue: #4860ff,
  blog-red: #f2434f,
  blog-purple: #7469ab,
  blog-gray: #a7abb5,
);

/** Bootstrap navbar fix (https://git.io/fADqW) */
$navbar-dark-toggler-icon-bg: none;
$navbar-light-toggler-icon-bg: none;

$grid-breakpoints: (
  xs: 0,
  sm:576px,
  md:768px,
  lg:1024px,
  xl:1200px,
  xxl:1440px,
  xxxl:1920px,
  //xxxxl:2400px
) !default;

$container-max-widths: (
  sm:540px,
  md:720px,
  lg:994px,
  xl:1170px,
  xxl:1280px,
  xxxl:1400px,
  //xxxxl:1600px
) !default;

$border-radius: 0.2rem;

// Update bootstrap spacer classes.
// https://medium.com/@louiswang0128/expand-bootstrap-spacing-classes-with-sass-ced4b5383aea
$spacer: 1rem !default;
$spacers: () !default;
$spacers: map-merge(
  (
    0: 0,
    1: ($spacer * 0.5),
    2: $spacer,
    3: ($spacer * 1.5),
    4: ($spacer * 2),
    5: ($spacer * 2.5),
    6: ($spacer * 3),
    7: ($spacer * 3.5),
    8: ($spacer * 4),
    9: ($spacer * 4.5),
    10: ($spacer * 5),
    11: ($spacer * 5.5),
    12: ($spacer * 6),
    13: ($spacer * 6.5),
    14: ($spacer * 7),
    15: ($spacer * 7.5),
    16: ($spacer * 8),
  ),
  $spacers
);
","/** Import Bootstrap functions */
@import "~bootstrap/scss/functions";

$text-lg: 1.5rem;

$headline-size: (
  large: 60px
);

$font-fallback: (
  serif: "serif",
  sans-serif: "sans-serif"
);

$font-midstack: (
  serif: "poynter-oldstyle-display",
  sans-serif: "nimbus-sans"
);

$font-main: (
  serif: "Times New Roman",
  sans-serif: "SoleilBk",
  headline: "Soleil-Bold",
  headline-light: "SoleilBk",
  tab-nav: "SoleilBk",
  superheadline: "SoleilBk",
  subheadline: "Soleil",
  list-headline: "SoleilSb",
  list-text: "Soleil",
  nav: "SoleilBk",
  nav-headline: "Soleil-Bold",
  nav-desc: "Soleil",
  body: "Soleil",
  cta: "SoleilBk",
  quote: "Soleil",
  //quote: "SoleilLt-Italic",
  quote-name: "SoleilSb",
  quote-details: "Soleil",
  strong: "Solie-Bold",
  section-header-light: "SoleilBk"
);

$link-colors: (
  link-lighter: #8c919e,
  link-lighter-hover: darken(#8c919e, 10),
  link-lighter-light-hover: lighten(#8c919e, 10),
  link-light: #717885,
  link-light-hover: darken(#717885, 10),
  link-light-light-hover: lighten(#717885, 10),
  link-dark: lighten(#1f2532, 20),
  link-dark-hover: lighten(#1f2532, 10),
  link-dark-light-hover: lighten(#1f2532, 35),
  link-white: #fff,
  link-gray-light: darken(#fff, 15),
  link-blue: #1d91d0,
  link-blue-hover: darken(#1d91d0, 20),
  link-primary: theme-color("primary"),
  link-primary-hover: theme-color("primary-dark"),
  light: theme-color("text-light"),
  lighter: theme-color("text-lighter"),
  dark: theme-color("dark"),
  darker: theme-color("darker")
  //link-secondary: #6c6463,
  //link-secondary-hover: darken(#6c6463, 10),
  //link-secondary-light-hover: lighten(#6c6463, 10)
);

$text-colors: (
  text-white: #fff,
  //dark: #0d2c3d,
  dark: theme-color("dark"),
  darker: theme-color("darker"),
  medium: #4f4f4f,
  light: theme-color("text-light"),
  lighter: theme-color("text-lighter"),
  primary: theme-color("primary"),
  secondary: theme-color("secondary"),
  nav-link-desc: #585e6f,
  nav-bg-dark-text: #c4c7cd,
  nav-link-gray: #565c77,
  nav-link-gray-hover: theme-color("primary")
);

$btn-colors: (
  dark: theme-color("dark"),
  light: theme-color("light"),
  btn-white: #fff,
  primary: theme-color("primary"),
  secondary: theme-color("secondary"),
  primary-alt: #4f4f4f,
  primary-alt-hover: lighten(#4f4f4f, 10)
);

$bg-colors: (
  bg-white: #fff,
  bg-dark: theme-color("dark"),
  bg-darker: theme-color("darker"),
  bg-light: theme-color("light"),
  bg-lighter: theme-color("lighter"),
  bg-primary: theme-color("primary"),
  bg-secondary: theme-color("secondary"),
);

$border-colors: (
  border-white: #fff,
  border-primary: theme-color("primary"),
  border-secondary: theme-color("secondary"),
  border-light:  theme-color("light"),
  border-dark:  #757575, // dark border take from mockup 2020-07-12
  border-lighter:  theme-color("lighter"),
  primary: theme-color("primary"),
  dark: theme-color("dark"),
  darker: theme-color("darker"),
  //light: theme-color("light"),
  //lighter: theme-color("lighter"),
  medium: #4f4f4f,
  light: #717885,
  lighter: #8c919e,
  secondary: theme-color("secondary")
);

$social-colors: (
  linkedin: #0072b1,
  facebook: #3b5998,
  twitter: #00acee,
);
