  color: text-color("dark");

  .headline {
    color: text-color("dark");
  }
}

.bg-gray {
  background-color: theme-color("gray");
}

.bg-secondary {
  background-color: theme-color("secondary");
  color: text-color("text-white");
}

.bg-sec-green,
.bg-sec-purple,
.bg-darker,
.bg-dark {
  background-color: theme-color("dark");
  color: text-color("text-white");

  .headline {
    color: text-color("text-white");
  }

  .card {
    &:not(.transparent) {
      color: text-color("dark");
    }
  }

  .text-col {
    p {
      color: text-color("lighter");
    }
  }

  .col-copy {
    .text-col {
      p {
        color: text-color("lighter");
      }
    }
  }

  .header-content {
    .headline {
      .superheadline {
        color: text-color("light");
      }

      .subheadline {
        color: text-color("light");
      }
    }
  }

  .bg-white {
    .headline {
      color: text-color("dark");
    }

    .col-copy {
      .text-col {
        p {
          color: text-color("dark");
        }
      }
    }
  }

  .bg-gray {
    .headline {
      color: text-color("dark");
    }

    .col-copy {
      .text-col {
        p {
          color: text-color("dark");
        }
      }
    }
  }

  .bg-light {
    .headline {
      color: text-color("dark");
    }

    .col-copy {
      .text-col {
        p {
          color: text-color("dark");
        }
      }
    }
  }

  .bg-lighter {
    .headline {
      color: text-color("dark");
    }

    .col-copy {
      .text-col {
        p {
          color: text-color("dark");
        }
      }
    }
  }

  .bg-lighter-to-white {
    .headline {
      color: text-color("dark");
    }

    .col-copy {
      .text-col {
        p {
          color: text-color("dark");
        }
      }
    }
  }

  .bg-light-to-white {
    .headline {
      color: text-color("dark");
    }

    .col-copy {
      .text-col {
        p {
          color: text-color("dark");
        }
      }
    }
  }

  .bg-white-to-lighter {
    .headline {
      color: text-color("dark");
    }

    .col-copy {
      .text-col {
        p {
          color: text-color("dark");
        }
      }
    }
  }

  .bg-white-to-light {
    .headline {
      color: text-color("dark");
    }

    .col-copy {
      .text-col {
        p {
          color: text-color("dark");
        }
      }
    }
  }
}

