.pretails {
  font-size: 14px;
  flex-wrap: wrap;

  .pretail-item {
    display: inline-block;
    color: theme-color("text-light");
    text-transform: uppercase;

    .category-list {
      list-style: none !important;
      padding: 0 !important;
      margin: 0 !important;

      li {
        display: inline-block;
        margin: 0;
        padding: 0;
        text-transform: uppercase;
        color: theme-color("text-light");

        &::after {
          content: ",\00a0";
          display: inline-block;
        }

        &:last-child {
          &::after {
            display: none;
          }
        }
      }
    }
  }

  .pretail-categories {
    & + .pretail-read-time {
      &::before {
        content: "\00a0-\00a0";
        display: inline-block;
        color: theme-color("text-light");
        text-transform: uppercase;
      }
    }
  }
}

