  .col-articles {
    > article {
      &.article-feed-item {
        &:nth-child(odd) {
          // First item
          // Put image to the left

          .col-image {
            order: 10;
          }

          .col-text {
            order: 1;

            .col-text-inside {
              margin-left: 1.5rem;
            }
          }
        }

        &:nth-child(even) {
          // Second item
